import React from 'react';
import PopupWithForm from './PopupWithForm';

function EditProfilePopup(props) {
    const { isOpen, onClose } = props;

    return (
        <PopupWithForm
        name='edit'
        title='Редактировать профиль'
        buttonText='Сохранить'

        isOpen={isOpen}
        onClose={onClose}
        >

                <div>
                    <input id="name-input" type="text" name="name" className="popup__input " minLength="2" maxLength="40" placeholder="Введите имя"
                    required />
                    <span className="name-input-error"></span>
                    <input id="job-input" type="text" name="profession" className="popup__input" minLength="2" placeholder="Ваша профессия"
                    maxLength="200" required />
                    <span className="job-input-error"></span>
                </div>

</PopupWithForm>
    )
}

export default EditProfilePopup;
