import React from 'react';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';

function App() {

  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);

  const [selectedCard, setSelectedCard] = React.useState({ name: '', link: '' });

  function handleEditAvatarClick() {
      setIsEditAvatarPopupOpen(true);
  };

  function handleEditProfileClick() {
      setIsEditProfilePopupOpen(true);
  };

  function handleAddPlaceClick() {
      setIsAddPlacePopupOpen(true);
  };

  function closeAllPopups() {
      setIsEditAvatarPopupOpen(false);
      setIsEditProfilePopupOpen(false);
      setIsAddPlacePopupOpen(false);

      setSelectedCard({ name: '', link: '' });
  }

  function handleCardClick(selectedCard) {
      setSelectedCard({ name: selectedCard.name, link: selectedCard.link });
  }

  return (

    <div className="root">

    <Header />

    <Main 
     onEditAvatar={handleEditAvatarClick}
     onEditProfile={handleEditProfileClick}
     onAddPlace={handleAddPlaceClick}

     onCardClick={handleCardClick}
    />

    <Footer />


    <div className="popup" id="imagePlacePopup" tabindex="-1">
        <div className="popup__container popup__container_type_preview">
            <img className="popup__image" alt="Архыз"
                src="https://pictures.s3.yandex.net/frontend-developer/cards-compressed/arkhyz.jpg" />
            <button type="button" class="button popup__close-button"></button>
            <h2 className="popup__header popup__header_type_preview"></h2>
        </div>
    </div>


    <template id="place-template">
        <div className="place">
            <button type="button" className="button place__bucket"></button>
            <button className="button">
                <img alt="_" src="_" class="place__image" />
            </button>
            <div className="place__header">
                <h2 className="place__title">
                </h2>
                <div className="place__likes-container">
                    <button type="button" className="button place__button-like"></button>
                    <span className="place__likes-count"></span>
                </div>

            </div>
        </div>

    </template>
    </div>
  );
}

export default App;
