import React from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext';

function Card(props) {
    const currentUser = React.useContext(CurrentUserContext);

    const isOwn = props.card.owner._id === currentUser._id;
    const isLiked = props.card.likes.some(i => i._id === currentUser._id);


    const cardDeleteButtonClassName = (
        `photo__trash ${isOwn ? 'photo__trash_visible' : 'photo__trash_hidden'}`
      ); 
    const cardLikeButtonClassName = isLiked ?
      'photo__like photo__like_active'
    :
      'photo__like';  

    function handleLikeClick() {
        onCardLike(card);
      }
      
    function handleDeleteClick() {
        props.onDeleteClick(props.card);
      }  

    function handleClick() {
        onCardClick(card);
        console.log(card);
    }

        return (
            <article className="photo">
                <div className="photo__image-container">
                    <button 
                    className={cardDeleteButtonClassName}
                    onClick={handleDeleteClick} 
                    aria-label="Удалить" 
                    type="button">

                    </button>
                    <img
                        className="photo__image photo__image_open"
                        src={card.link} alt={card.name}
                        onClick={handleClick} />
                </div>
                <h3
                    className="photo__title"
                    name="link">{card.name}</h3>
                <div className="photo__like-container">
                    <button
                    onClick={handleLikeClick} 
                    className={cardLikeButtonClassName} 
                    type="button" 
                    aria-label="Нравится">

                    </button>
                    <p className="photo__like-counter">{card.likes.length}</p>
                </div>
            </article>
        )
}

export default Card
