import React from 'react';
import Card from './Card';
import {api} from './../utils/Api';

function Main (props) {
    const { onEditAvatar, onEditProfile, onAddPlace, onCardClick } = props;

    const [userName, setUserName] = React.useState('');
    const [userDescription, setUserDescription] = React.useState('');
    const [userAvatar, setUserAvatar] = React.useState('');

    const [cards, setCards] = React.useState([]);

    React.useEffect(() => {
        api.getUserInformation()
            .then((card) => {
                setUserName(card.name);
                setUserDescription(card.about);
                setUserAvatar(card.avatar);
            })
            .catch((err) => {
                console.log(err);
            })
    }, []);

    React.useEffect(() => {
        api.getInitialCards()
            .then((cards) => {
                console.log(cards);
                setCards(cards);
            })
            .catch((err) => {
                console.log(err);
            })
    }, []);


    return (
<main>
        <section className="profile root__profile">
            <div className="profile__avatar-container">
                <button className="button profile__avatar-edit-button" onClick={onEditAvatar}></button>
                <img className="profile__avatar" 
                src={userAvatar}
                alt="Ваше фото"/>
            </div>
            <div className="profile__info-block">
                <div className="profile__info">
                    <h1 className="profile__name">{userName}</h1>
                    <button type="button" className="button profile__edit-button" onClick={onEditProfile}></button>
                </div>
                <p className="profile__profession">{userDescription}</p>
            </div>
            <button type="button" className="profile__add-button button" onClick={onAddPlace}></button>

        </section>
        <section className="place-container root__place-container">
        </section>

        <template id="place-template">
        <div className="place">
                {cards.map(card => <Card
                    card={card}
                    key={card._id}
                    onCardClick={onCardClick} />)}
        </div>

        </template>
    </main>
    )
}

export default Main
