import React from "react";
import Card from "./Card.js";
import { CurrentUserContext } from "../contexts/CurrentUserContext";

function Main(props) {
  /*
  const { onEditAvatar, onEditProfile, onAddPlace, onCardClick } = props;

  const [userName, setUserName] = React.useState("");
  const [userDescription, setUserDescription] = React.useState("");
  const [userAvatar, setUserAvatar] = React.useState("");
*/

  const currentUser = React.useContext(CurrentUserContext);

  /*
  React.useEffect(() => {
    api
      .getUserInformation()
      .then((card) => {
        setUserName(card.name);
        setUserDescription(card.about);
        setUserAvatar(card.avatar);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  React.useEffect(() => {
    api
      .getInitialCards()
      .then((cards) => {
        console.log(cards);
        setCards(cards);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
*/
  return (
    <main>
      <section className="profile">
        <div className="profile__container">
          <div className="profile__photo-wrapper" onClick={props.onEditAvatar}>
          <img
            className="profile__photo"
            src={props.avatar}
            alt="Аватар"
          />
          </div>

          <div className="profile__info">
            <h1 className="profile__name">{props.name}</h1>
            <button
              className="profile__btn-edit"
              type="button"
              value=""
              onClick={props.onEditProfile}
            ></button>
            <p className="profile__about">{props.about}</p>
          </div>

          <button
            className="profile__btn-add"
            type="button"
            onClick={props.onAddPlace}
          ></button>
        </div>
      </section>

      <section className="photos">
        {props.map((card) => (
          <Card
            card={card}
            key={card._id}
            onCardClick={props.onCardClick}
            onCardLike={props.onCardLike}
            onDeleteClick={props.onDeleteClick}
          />
        ))}
      </section>
    </main>
  );
}

export default Main;
